import React, { Component } from 'react'
import { thisExpression } from '@babel/types';

export class AddTask extends Component {
    constructor(props) {
        super(props)

        this.state = {
             name:'',
             status:''
        }
    }
    onHandleChange= (event) =>{
        var target =  event.target;
        var name = target.name;
        var status = target.value;
        this.setState({
            [name]:status
        })
    }
    onSubmit = (event) =>{
        event.preventDefault();
        console.log(this.state);
    }
    render() {

        return (
            <div>

                    <div className="form-group">
                        <label ><h2>Input form</h2></label><br></br>
                        <label ><h3>Name: </h3></label>
                        <input onChange={this.onHandleChange} type="text" name="name"className="form-control" placeholder="" aria-describedby="helpId"></input>
                        <label><h3>Status: </h3></label>
                    <div className="form-group" >

                        <select className="form-control" name="status" onChange={this.onHandleChange}>
                        <option value={1}>Hidden</option>
                        <option value={2}>Active</option>

                        </select><br></br>
                        <button className="btn btn-warning" type="submit" onClick={this.onSubmit}>Save</button>
                        <button className="btn btn-danger">Cancel</button>

                    </div>
                </div>
                    </div>

        )
    }
}

export default AddTask
