import React, { Component } from 'react'


export class TaskForm extends Component {
    constructor(props) {
        super(props)

        this.state = {
             name:'',
             status:"false"
        }
    }
    onHandleChange= (event) =>{
        var target =  event.target;
        var name = target.name;
        var value = target.value;
        if(name==="status")
            value==="true" ? value=true : value=false;  
        this.setState({
            [name]:value
        })
    }
    onCloseForm = () =>{
        this.props.onRecieveCloseTaskForm();
    }
    onSubmit = (event) =>{
        event.preventDefault();
        console.log(this.state);
        this.props.onReceive(this.state);
        this.onClear();
        this.onCloseForm();
        alert("Saved");

    }
    onClear = () => {
        this.setState({
            name:'',
            status:false
        });
        this.onCloseForm();
    }

    render() {

        return (
            <div className="border border-dark container-fluid">

                    <form className="form-group  " onSubmit={this.onSubmit}>
                        <label ><h2>Input form</h2></label><br></br>
                        <label ><h3>Name: </h3></label>
                        <input 
                                type="text" 
                                onChange={this.onHandleChange} 
                                value={this.state.name}
                                name="name"className="form-control" placeholder="" aria-describedby="helpId"></input>
                        <label><h3>Status: </h3></label>
                    <div className="form-group text-center " >

                        <select className="form-control" 
                            name="status" 
                            value={this.state.status} 
                            onChange={this.onHandleChange}>
                        <option value={false}>Hidden</option>
                        <option value={true}>Active</option>

                        </select><br></br>
                        <button className="btn btn-warning" type="submit" >Save</button>
                        <button className="btn btn-danger" 
                                type="reset"
                                onClick={this.onClear}>Cancel</button>

                    </div>
                    </form>
                    </div>

        )
    }
}

export default TaskForm
