import React, { Component } from 'react'

export class TaskItem extends Component {
    render() {
        var {task , index }=this.props;
        return (

                <tr>

                            <td>{index+1}</td>
                            <td>{task.name}</td>
                            <td>
                                <span className={task.status===true ? "label label-danger" : "label label-success"}></span>
                            </td>

                </tr> 

        )
    }
}

export default TaskItem
