import React, { Component } from 'react'

import Search from './Components/Search'
import TaskForm from './Components/TaskForm'
import Control from './Components/Control';
import Sort from './Components/Sort';

export class App extends Component {
            constructor(props) {
                super(props)
                this.state = {
                     tasks:[],
                     isDisplayForm:true
                }
            }
            componentWillMount(){
                if(localStorage && localStorage.getItem('tasks'))
                {
                    var tasks = JSON.parse(localStorage.getItem('tasks'));
                    this.setState({
                        tasks:tasks
                    });
                }
            }
            generateDate = () =>{
                var tasks= [
                    {
                        id: this.createID(),
                        name: "html",
                        status: true
                    },
                    {
                        id: this.createID(),
                        name: "CSS",
                        status: false
                    },
                    {
                        id: this.createID(),
                        name: "Angular",
                        status: true
                    }
                ];
                console.log(tasks);
                this.setState({
                    tasks : tasks
                });
                localStorage.setItem('tasks',JSON.stringify(tasks));
            };

            randomString = () => {
                var randomstring = require("randomstring");
                return randomstring.generate();
            }
            createID = () =>{
                return this.randomString()+"-"+this.randomString();
            }
            ChangeDisplay =()=>{
                this.setState({isDisplayForm:!this.state.isDisplayForm});
            }
            onReceive = (data) => {

                    var newtask=this.state.tasks;
                    data.id=this.createID();
                    newtask.push(data)
                    this.setState({
                        tasks:newtask
                    });
                    //localStorage.setItem("tasks",JSON.stringify)
                }


  render() {
           var {tasks,isDisplayForm} = this.state; //var tasks = this.state.tasks
           var elmTaskForm = isDisplayForm  ? <TaskForm onReceive={this.onReceive}/> : ""  ;
    return (
        <div className="container border border-danger">
          <h1 className="text-center">Task Manager</h1>
          <hr></hr>
        {/** top page */}
        {/**start of page */}
        <div className="row">
            <div className={isDisplayForm===true ? "col-xs-4 col-md-4  col-lg-4" : ""} >
                {elmTaskForm}
            </div>
            <div className={isDisplayForm===true ? "col-xs-8 col-md-8 col-lg-8" : "col-xs-12 col-md-12 col-lg-12"}>
                <p>
                    <button className="btn btn-primary" type="button" onClick={this.ChangeDisplay}aria-expanded="false"
                            aria-controls="contentId">
                        Add
                    </button>
                    <button className="btn btn-danger" onClick={this.generateDate} type="button"  aria-expanded="false">
                        Generate Data
                    </button>
                </p>
                <div className="row">
                    <Search />
                    <Sort />
                </div>

                <div className="row">
                    <Control tasks={tasks}/>
                </div>
            </div>
        </div>



        </div>
    )
  }
}

export default App
