import React, { Component } from 'react'

import SearchAndSort from './Components/SearchAndSort'
import TaskForm from './Components/TaskForm'

export class App extends Component {
            constructor(props) {
                super(props)

                this.state = {
                     tasks:[
                         {
                             task_name:'',
                             task_status:''
                         }
                     ]
                }
            }

  render() {

    return (
        <div className="container">
          <h1 className="text-center">Task Manager</h1>
          <hr></hr>
        {/** top page */}
        {/**start of page */}
        <div className="row">
            <div className="col-xs-4 col-md-4 collapse" id="contentId">
                <TaskForm  />
            </div>
            <div className="col-xs-8 col-md-8">
                <p>
                    <button className="btn btn-primary" type="button" data-toggle="collapse" data-target="#contentId" aria-expanded="false"
                            aria-controls="contentId">
                        Add
                    </button>
                </p>
                <SearchAndSort />
                <div className="row">
                    <TaskForm />
                </div>
            </div>
        </div>



        </div>
    )
  }
}

export default App
